import styled from 'styled-components';

export const CheckoutItemContainer = styled.div`
.CheckoutItem {
    width: 100%;
    display: flex;
    min-height: 100px;
    border-bottom: 1px solid darkgrey;
    padding: 15px 0;
    font-size: 20px;
    align-items: center;
  
    .name,
    .quantity,
    .price {
      width: 23%;
    }
  
    .quantity {
      padding-left: 20px;
      display: flex;
    }

    .arrow {
      cursor: pointer;
    }

    .value {
      padding: 0px 10px;
    }
  
    .remove-button {
      padding-left: 12px;
      cursor: pointer;
    }
  }
  
`;

export const ImageContainer = styled.div`
      width: 23%;
      padding-right: 15px;
  
      img {
        width: 100%;
        height: 100%;
      }
`;
