import styled from 'styled-components';


export const MenuItemContainer = styled.div`
    min-width: 30%;
    height: 200px;
    flex: 1 1 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid black;
    margin: 0 7.5px 15px;
    overflow: hidden;

    &:hover {
        cursor: pointer;
      
        & .background-image {
          transform: scale(1.1);
          transition: transform 6s cubic-bezier(0.25, 0.45, 0.45, 0.95);
        }
      
        & .content {
          opacity: 0.9;
        }
      }
      

    &.large {
        height: 380px;
    }
  
    &:first-child {
      margin-right: 7.5px;
    }
  
    &:last-child {
      margin-left: 7.5px;
    }

    @media screen and (min-width: 768px) {
      height: ${({size}) => (size? '380px' : '240px')}
    }
`;

export const BackgroundImage = styled.div`
        width: 100%;
        height: 100%;
        background-size: cover;
        background-position: center;
`;
  

export const ContentContainer = styled.div`
      height: 90px;
      padding: 0 25px;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      border: 1px solid black;
      background-color: white;
      opacity: 0.7;
      position: absolute;
  
      .title {
        font-weight: bold;
        margin: 0;
        margin-bottom: 6px;
        font-size: 22px;
        color: #4a4a4a;
      }
  
      .subtitle {
        font-weight: lighter;
        font-size: 16px;
      }
`;
